import React from 'react';
import { Text, View, TouchableOpacity, StyleSheet, TextInput, Image } from 'react-native';
import * as Permissions from 'expo-permissions';
import { BarCodeScanner } from 'expo-barcode-scanner';
import firebase from "firebase"
import db from "../config"

export default class TransactionScreen extends React.Component {
    constructor(){
      super();
      this.state = {
        hasCameraPermissions: null,
        scanned: false,
        scannedData: '',
        buttonState: 'normal',
        scannedBookID: "",
        scannedStudentID:""
      }
    }

    getCameraPermissions = async (ID) =>{
      const {status} = await Permissions.askAsync(Permissions.CAMERA);
      
      this.setState({
        /*status === "granted" is true when user has granted permission
          status === "granted" is false when user has not granted the permission
        */
        hasCameraPermissions: status === "granted",
        buttonState: ID,
        scanned: false
      });
    }

    handleBarCodeScanned = async({type, data})=>{
      const {buttonState} = this.state
      if(buttonState === "bookID"){

      
      this.setState({
        scanned: true,
        scannedData: data,
        buttonState: 'normal'
      });
    }
  }

  handleTransaction = ()=>{
    var transactionMessage;
    db.collection("books").doc(this.state.scannedBookID).get()
    .then((doc)=>{
      var book = doc.data();
      if (book.bookAvailability === true){
        this.initiateBookIssue();
        transactionMessage = "bookIssue"
      } else {
        
      }
    })
  }

    render() {
      const hasCameraPermissions = this.state.hasCameraPermissions;
      const scanned = this.state.scanned;
      const buttonState = this.state.buttonState;

      if (buttonState !== "normal" && hasCameraPermissions){
        return(
          <BarCodeScanner
            onBarCodeScanned={scanned ? undefined : this.handleBarCodeScanned}
            style={StyleSheet.absoluteFillObject}
          />
        );
      }

      else if (buttonState === "normal"){
        return(
          <View style={styles.container}>
            <Image source = {require("../assets/booklogo.jpg")} style= {{width : 100,height:100}} />
          <View style={styles.inputView}>
            <TextInput style={styles.inputBox} placeholder = "bookID" value={this.state.scannedBookID} />
            <TouchableOpacity style = {styles.scanButton} onPress={()=>{
              this.getCameraPermissions("bookID");
            }} >
              <Text style={styles.buttonText}>Scan</Text>
            </TouchableOpacity>
          </View>
          <View style={styles.inputView}>
            <TextInput style={styles.inputBox} placeholder="studentID" value={this.state.scannedStudentID} />
            <TouchableOpacity style={styles.scanButton} onPress={()=>{
              this.getCameraPermissions("studentID")
            }} >
              <Text style={styles.buttonText}>Scan</Text>
            </TouchableOpacity>
          </View>
          <TouchableOpacity style={styles.submitButton} onPress={()=>{
            this.handleTransaction()
          }}>
            <Text style={styles.submitButtonText}>Submit</Text>
          </TouchableOpacity>
        </View>
        );
      }
    }
  }

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center'
    },
    displayText:{
      fontSize: 15,
      textDecorationLine: 'underline'
    },
    
    buttonText:{
      fontSize: 20,
    },
    inputView:{ flexDirection: 'row', margin: 20 }, 
    inputBox:{ width: 200, height: 40, borderWidth: 1.5, borderRightWidth: 0, fontSize: 20 }, 
    scanButton:{ backgroundColor: '#66BB6A', width: 50, borderWidth: 1.5, borderLeftWidth: 0 },
    submitButton : {backgroundColor:"red",width:80,height:45, alignItems:"center", marginLeft:20, justifyContent:"center"},
    submitButtonText:{textAlign:"center", fontSize:22, color : "white"}
  });
